import React, { Component } from 'react';

class buttons extends Component {

    isOperator = val => {
        return !isNaN(val) || val === "." || val === "=";
    }
    render() { 
        return (<div 
            className={`btn btn-secondary w-25 ${this.isOperator
            (this.props.children) ? "" : "btn btn-warning w-25"}`}
            onClick={() => this.props.handleClick
            (this.props.children)}
        >
            {this.props.children}
        </div>  );
    }
}
 
export default buttons;
