import React, { Component } from 'react';

class buttons extends Component {

    isOperator = val => {
        return !isNaN(val) || val === "." || val === "=";
    }
    render() { 
        return (<div className={`button ${this.isOperator(this.props.children) ? "" : "operator"}`}
        >
            {this.props.children}
        </div>  );
    }
}
 
export default buttons;
