import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';


class calculator extends Component {

state = {
  value1: null,
  storage: null
}



addToInput = val => {
  if(this.state.value1 == null){
    this.setState({value1: val});
  }else{
    this.setState({value1: this.state.value1 + "" + val});
  }
}

clear = () =>{
  this.setState({
    value1: ""
  })
}

plus = () =>{
  const store = this.state.value1 + va;
this.setState({
  storage : store,
  value1: ""
})
}

  render() { 
    return (

      <div className="container mt-5 w-25">
          <div className="card">
            <div className="card-body">
              <div className="row">
                <div className="col-12 text-center border rounded-pill">
                 <h3>CALCULATOR using ReactJS</h3>
                </div>
                <div className="col-12 mt-2">
                  <input className="form-control" value={this.state.value1} readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-dark w-25" value='+' onClick={this.plus} readOnly></input>
                <input className="btn btn-dark w-25" value='x' readOnly></input>
                <input className="btn btn-dark w-25" value='-' readOnly></input>
                <input className="btn btn-dark w-25" value='/' readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-secondary w-25" value='6' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-secondary w-25" value='7' onClick={() => this.addToInput(7)}readOnly></input>
                <input className="btn btn-secondary w-25" value='8' onClick={() => this.addToInput(8)} readOnly></input>
                <input className="btn btn-secondary w-25" value='9' onClick={() => this.addToInput(9)} readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-secondary w-25" value='2' onClick={() => this.addToInput(2)} readOnly></input>
                <input className="btn btn-secondary w-25" value='3' onClick={() => this.addToInput(3)} readOnly></input>
                <input className="btn btn-secondary w-25" value='4' onClick={() => this.addToInput(4)} readOnly></input>
                <input className="btn btn-secondary w-25" value='5' onClick={() => this.addToInput(5)} readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-secondary w-25" value='0' onClick={() => this.addToInput(0)} readOnly></input>
                <input className="btn btn-secondary w-25" value='1' onClick={() => this.addToInput(1)} readOnly></input>
                <input className="btn btn-warning w-25" value='CE'onClick={this.clear} readOnly></input>
                <input className="btn btn-info w-25" value='=' readOnly></input>
                </div>
              </div>
            </div>
          </div>
      </div>  
      );

  }
}

export default calculator;
