import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Button from './components/button';
import ClearButton from './components/clearButton.jsx';
import Input from './components/input';

class calculator extends Component {
state = {
  input: "",
  var1: "",
  var2: "",
  operator: ""
}

  addToInput = (val) =>{
      this.setState({
         input: this.state.input + val
      })
  }

  addZeroToInput = (val) =>{
    if(this.state.input !== ""){
      this.setState({
         input: this.state.input + val
      })
    } 
  }

  addDecimal = (val) =>{
    if(this.state.input.indexOf(".") === -1){
      this.setState({input: this.state.input + val})
    }
  }

  clearInput = () =>{
    this.setState({
      input: ""
    })
  }

  add = () =>{
    this.state.var1 = this.state.input
    this.setState({
      input: ""
    });
    this.state.operator = "plus";
  }

  minus = () =>{
    this.state.var1 = this.state.input
    this.setState({
      input: ""
    });
    this.state.operator = "minus";
  }

  multiply = () =>{
    this.state.var1 = this.state.input
    this.setState({
      input: ""
    });
    this.state.operator = "multiply";
  }

  evaluate = () =>{
    this.state.var2 = this.state.input;

    if(this.state.operator == "plus"){
      this.setState({
        input: parseInt(this.state.var1) + parseInt(this.state.var2)
      })
    }else if(this.state.operator == "minus"){
      this.setState({
        input: parseInt(this.state.var1) - parseInt(this.state.var2)
      })
    }else if(this.state.operator == "multiply"){
      this.setState({
        input: parseInt(this.state.var1) * parseInt(this.state.var2)
      })
    }
  }
  render() { 
    return (

      <div className="container mt-5 w-25">
          <div className="card">
            <div className="card-body">
              <div className="row">
                <div className="col-12 text-center border rounded-pill">
                 <h3>CALCULATOR using ReactJS</h3>
                </div>
                <div className="col-12 mt-2">
                    <Input>{this.state.input}</Input>
                </div>
                <div className="col-12 mt-2">
                    <Button handleClick={this.addToInput}>7</Button>
                    <Button handleClick={this.addToInput}>8</Button>
                    <Button handleClick={this.addToInput}>9</Button>
                    <Button handleClick={this.divide}>/</Button>
                </div>
                <div className="col-12 mt-2">
                    <Button handleClick={this.addToInput}>4</Button>
                    <Button handleClick={this.addToInput}>5</Button>
                    <Button handleClick={this.addToInput}>6</Button>
                    <Button handleClick={this.multiply}>*</Button>
                </div>
                <div className="col-12 mt-2">
                    <Button handleClick={this.addToInput}>1</Button>
                    <Button handleClick={this.addToInput}>2</Button>
                    <Button handleClick={this.addToInput}>3</Button>
                    <Button handleClick={this.add}>+</Button>
                </div>
                <div className="col-12 mt-2">
                    <Button handleClick={this.addDecimal}>.</Button>
                    <Button handleClick={this.addZeroToInput}>0</Button>
                    <Button handleClick={this.evaluate}>=</Button>
                    <Button handleClick={this.minus}>-</Button>
                    <ClearButton handleClear={this.clearInput}></ClearButton>
                </div>
              </div>
            </div>
          </div>
      </div>  
      );

  }
}

export default calculator;
