import React, { Component, useRef } from "react";
import { MDBNavbar, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink } from 'mdbreact';
import { BrowserRouter as Router, Link } from 'react-router-dom';

class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
      isWideEnough: false,
    };
    this.onClick = this.onClick.bind(this);
  }

  handleHi = () => {
    const hi = useRef();
     const handleClick = () => {
       window.scrollTo(0, hi.current.offsetTop);
     };
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse,
    });
  }

  render() {
    return (
      <div>
        <header>
          <Router>
            <MDBNavbar
              color="bg-light"
              fixed="top"
              dark
              expand="md"
              scrolling
              transparent
            >
              {!this.state.isWideEnough && (
                <MDBNavbarToggler onClick={this.onClick} />
              )}
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav center>
                  <MDBNavItem>
                    <MDBNavLink>
                      <Link to="#hi"></Link>Hi
                    </MDBNavLink>
                  </MDBNavItem>
                  {/*<MDBNavItem>
                  <MDBNavLink to="#">About me</MDBNavLink>
                </MDBNavItem> */}
                  <MDBNavItem>
                    <MDBNavLink to="#">Badge</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="#hello">Project</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="#hello">Get in Touch</MDBNavLink>
                  </MDBNavItem>
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
          </Router>
        </header>
      </div>
    );
  }
}

export default Menu;
