import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';


class calculator extends Component {


  constructor(props)
    {
      super(props); 
      this.state = {
          inputed: ""
      }
    }

addToInput = val => {
  console.log(val)
this.setState({inputed: this.state.inputed + val});
}

  render() { 
    return (

      <div className="container mt-5 w-25">
          <div className="card">
            <div className="card-body">
              <div className="row">
                <div className="col-12 text-center border rounded-pill">
                 <h3>CALCULATOR using ReactJS</h3>
                </div>
                <div className="col-12 mt-2">
                  <input className="form-control" value={this.state.inputed} readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-dark w-25" value='+' readOnly></input>
                <input className="btn btn-dark w-25" value='x' readOnly></input>
                <input className="btn btn-dark w-25" value='-' readOnly></input>
                <input className="btn btn-dark w-25" value='/' readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-secondary w-25" value='6' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-secondary w-25" value='7' onClick={() => this.addToInput(7)}readOnly></input>
                <input className="btn btn-secondary w-25" value='8' onClick={() => this.addToInput(8)} readOnly></input>
                <input className="btn btn-secondary w-25" value='9' onClick={() => this.addToInput(9)} readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-secondary w-25" value='2' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-secondary w-25" value='3' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-secondary w-25" value='4' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-secondary w-25" value='5' onClick={() => this.addToInput(6)} readOnly></input>
                </div>
                <div className="col-12 mt-2">
                <input className="btn btn-secondary w-25" value='0' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-secondary w-25" value='1' onClick={() => this.addToInput(6)} readOnly></input>
                <input className="btn btn-warning w-25" value='CE' readOnly></input>
                <input className="btn btn-info w-25" value='=' readOnly></input>
                </div>
              </div>
            </div>
          </div>
      </div>  
      );

  }
}

export default calculator;
